mapper.update:
UPDATE comment
SET type = 1
WHERE id = #{id}

UPDATE comment
SET type = 0
WHERE id = #{id}

mapper.select:
SELECT c.*, u.nickname
FROM comment c
         LEFT JOIN user u ON c.creator_id = u.id
WHERE c.blog_id = #{blogId}
ORDER BY c.type DESC,c.creation_time DESC
LIMIT 10 OFFSET #{pageOffset}

SELECT count(*)
FROM comment
WHERE blog_id = #{blogId}
